mapper.select:
select ids, paper_id, title, upload_time, upload_user_id, public, author from paper
where organize_id=#{organizeId}
and approve_time is null

mapper.update:
update paper set
update_time=now()


        ,title=#{title}




        ,content=#{content}




        ,status=#{status}




        ,approve_mark=#{approveRemark}




        ,approve_time=#{approveTime}




        ,category_id=#{categoryId}




        ,public =#{isPublic}




        ,img_url=#{imgUrl}




        ,file_url=#{fileUrl}




        ,approve_user_id=#{approveUserId}




        ,author=#{author}




        ,views=#{views}




        ,organize_id=#{organizeId}


where paper_id=#{paperId}

mapper.select:
select ids, paper_id, title, views, approve_time from paper
where 1=1
and status='通过审核'
and category_id=#{categoryId}
and organize_id=#{organizeId}
order by ids desc
limit #{offset},#{size}
